import 'dart:convert';

import 'package:flutter/material.dart';
import 'package:flutter_mold/common/util.dart';
import 'package:flutter_mold/widgets/table.dart';
import 'package:flutter_mold/widgets/text.dart';

class MyAvatar extends StatelessWidget {
  double radius;
  String imageUrl;
  final String text;

  MyAvatar({
    required this.radius,
    required this.imageUrl,
    this.text = "",
  });

  final String avatarBase64 = """
  
  """;

  @override
  Widget build(BuildContext context) {
    if (radius == null || radius == 0 || radius < 0) {
      radius = 20;
    }

    Widget widget;
    if (imageUrl != null && imageUrl.isNotEmpty) {
      widget = ClipRRect(
        child: Image.network(
          imageUrl,
          width: radius * 2,
          height: radius * 2,
          fit: BoxFit.cover,
          loadingBuilder: (_, widget, loading) {
            return MyTable([_buildWithText(), widget]);
          },
        ),
        borderRadius: BorderRadius.circular(radius),
      );
    } else {
      widget = _buildWithText();
    }

    if (true) {
      widget = ClipRRect(
        child: Container(
          color: Colors.white,
          width: (radius * 2) + 8,
          height: (radius * 2) + 8,
          child: Stack(
            children: [Center(child: widget)],
          ),
        ),
        borderRadius: BorderRadius.circular(radius + 8),
      );
    }

    return widget;
  }

  Widget _buildWithText() {
    if (Util.isEmpty(text)) {
      return _buildDefault();
    }
    return MyTable(
      [
        MyText(
          text.length > 1 ? text.substring(0, 1) : text,
          upperCase: true,
          textAlign: TextAlign.center,
          flex: 1,
        )
      ],
      borderRadius: BorderRadius.circular(radius),
      width: radius * 2,
      height: radius * 2,
      crossAxisAlignment: CrossAxisAlignment.center,
      mainAxisAlignment: MainAxisAlignment.center,
      background: getBackgroundColor(),
      padding: EdgeInsets.all(2),
    );
  }

  Widget _buildDefault() {
    final avatar = base64Decode(avatarBase64.trim());
    return ClipRRect(
      borderRadius: BorderRadius.circular(radius),
      child: Container(
        width: radius * 2,
        height: radius * 2,
        decoration: BoxDecoration(
          gradient: LinearGradient(
            colors: [
              Color(0xFFC5CCD1),
              Color(0xFFB1B8BC),
            ],
          ),
        ),
        child: Image.memory(avatar.buffer.asUint8List()),
      ),
    );
  }

  Color getBackgroundColor() {
    List<Color> colors = [
      Colors.amber,
      Colors.amberAccent,
      Colors.blue,
      Colors.blueAccent,
      Colors.yellowAccent,
      Colors.yellow,
      Colors.cyanAccent,
      Colors.blueGrey,
      Colors.deepOrange,
      Colors.deepOrangeAccent,
      Colors.deepPurple,
      Colors.deepPurpleAccent,
      Colors.green,
      Colors.greenAccent,
      Colors.purple,
      Colors.pink,
      Colors.teal,
    ];
    int nameFirstCode = text.codeUnits.first;
    return colors[nameFirstCode % colors.length];
  }
}
